/* Dashboard Styles */
.dashboard-container {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 1.5rem;
  margin-top: 2rem;
}

.card {
  background-color: white;
  border-radius: 8px;
  padding: 1.5rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.card:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 15px rgba(0, 0, 0, 0.1);
}

.card-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
  border-bottom: 1px solid #eee;
  padding-bottom: 0.5rem;
}

.card-title {
  font-size: 1.2rem;
  color: var(--primary-color);
  font-weight: bold;
}

.stats-container {
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
}

.stat-item {
  flex: 1;
  min-width: 120px;
  text-align: center;
  padding: 1rem;
  background-color: #f8f9fa;
  border-radius: 6px;
}

.stat-value {
  font-size: 2rem;
  font-weight: bold;
  color: var(--primary-color);
  display: block;
}

.stat-label {
  font-size: 0.9rem;
  color: var(--text-color);
  margin-top: 0.5rem;
}

.chart-container {
  height: 300px;
  margin: 1.5rem 0;
}

.cta-button {
  display: inline-block;
  padding: 0.75rem 1.5rem;
  background-color: var(--primary-color);
  color: white;
  border: none;
  border-radius: 4px;
  text-decoration: none;
  font-weight: 500;
  transition: background-color 0.3s;
  cursor: pointer;
}

.cta-button:hover {
  background-color: #2980b9;
}
